const promise = Promise.resolve(
    fetch('https://status.supabase.com/history.atom')
        .then(res => res.text())
        .then(xmlText => {
            const parser = new DOMParser();
            const doc = parser.parseFromString(xmlText, 'application/xml');

            const obrigatorios = ['Auth', 'Database', 'Storage', 'Edge functions'];
            const entries = Array.from(doc.querySelectorAll('entry'));

            const incidentesAtivos = entries.filter(entry => {
                const titulo = entry.querySelector('title')?.textContent || '';
                const conteudo = entry.querySelector('content')?.textContent.toLowerCase() || '';
                const servicoAfetado = obrigatorios.some(serv => titulo.includes(serv));
                const ativo = !conteudo.includes('resolved');
                return servicoAfetado && ativo;
            });

            variables['511287ce-b332-419b-89eb-ee4700b4054e'] =(incidentesAtivos.length > 0);
        })
);

