const objectiveList = context.item.data?.["objetivo"];
let totalPercentual = 0;
let totalKRs = 0;

if (objectiveList) {
  const allKRs = objectiveList.flatMap(obj => Array.isArray(obj?.list) ? obj.list : []);

  for (const kr of allKRs) {
    const atual = Number(kr?.atual);
    const alvo = Number(kr?.alvo);

    if (!isNaN(atual) && !isNaN(alvo) && alvo > 0) {
      let percentual = Math.min((atual / alvo) * 100, 100); // Máximo 100%
      totalPercentual += percentual;
      totalKRs++;
    }
  }
}

const progresso = totalKRs > 0 ? (totalPercentual / totalKRs).toFixed(2) : 0;
return `Progresso geral: ${progresso}%`;

