    let dataRecebidaObj = new Date(v.date);

    if (isNaN(dataRecebidaObj)) {
      v.visible = false;
      return v;
    }

    let dataAtual = new Date();

    v.visible = (dataRecebidaObj <= dataAtual && v.finish == "");

    return v;
  });
