let resultado =
  context.workflow["cb5ae646-d95b-491e-990c-aae68ac660b3"].result?.["data"]?.[
    "metaDado"
  ];

resultado = !Array.isArray(resultado) ? [resultado] : resultado;

const resultList = [];
// return resultado;

for (const resultSingle of resultado) {
  const { components } = resultSingle;

  const [ciclo] = components.filter((c) => c.nome == "Ciclo");

  const objetivos = components.filter((c) => c.nome == "Objetivos");

  let objetivosFormatted = [];

  objetivos.forEach((obj) => {
    const titulo = obj.data["Nome"];
    const descricao = obj.data["Descrição"];
    // Metas
    const components = obj.components ?? null;
    let objPorc = 0;
    let list = [];
    // Metas mount
    if (components) {
      const metas = components.filter((f) => f.nome == "Meta");
      metas.forEach((meta) => {
        if (meta.data) {
          const metaTitulo = meta.data["Nome"];
          const metaDesc = meta.data["Descrição"];
          const alvo = meta.data["Meta alvo"];
          const atual = meta.data["Meta atual"];
          let porc = 0;

          let resp = meta.data["Responsável"];
          if (resp && resp != "") {
            let uniqueUsers =
              formulas["a423d4b2-ef77-42a9-ae1b-ba3ce0a45318"](resp);
            resp = uniqueUsers;
          }

          if (alvo != 0 && atual != undefined && alvo != undefined) {
            porc = Math.round((parseFloat(atual) / parseFloat(alvo)) * 100);
          }

          list.push({
            id: meta.id,
            metaTitulo,
            metaDesc,
            alvo,
            porc,
            atual: atual ?? 0,
            resp: resp ?? undefined,
          });
        }
      });
    }

    objetivosFormatted.push({
      id: obj.id,
      titulo,
      descricao,
      list,
      porc: objPorc
    });
  });

  resultList.push({
    okrId: resultSingle.id,
    id: ciclo.id,
    ciclo: ciclo.data["Nome"],
    from: ciclo.data["Data de início"],
    to: ciclo.data["Data final"],
    objetivo: objetivosFormatted,
    visible: true,
    collapse: true,
  });
}

const sort = formulas["f626c000-8e63-4599-ae90-c8c9a23f8f07"];

return sort(resultList);

