function createReadOnlyDate(value) {
  // Extrai YYYY‑MM‑DD de ISO, se necessário
  let dateStr = value;
  if (typeof value === "string" && value.includes("T")) {
    dateStr = new Date(value).toISOString().split("T")[0];
  }
  const [year, month, day] = dateStr.split("-");
  const monthNames = [
    "Janeiro","Fevereiro","Março","Abril","Maio","Junho",
    "Julho","Agosto","Setembro","Outubro","Novembro","Dezembro"
  ];
  const dateText = `${Number(day)} ${monthNames[Number(month)-1]}, ${year}`;

  // Container
  const roDiv = document.createElement("div");
  roDiv.style.display = "flex";
  roDiv.style.alignItems = "center";
  roDiv.style.padding = "8px 12px";
  roDiv.style.marginBottom = "15px";
  roDiv.style.backgroundColor = "#f3f4f6";
  roDiv.style.border = "1px solid #e5e7eb";
  roDiv.style.borderRadius = "8px";
  roDiv.style.gap = "8px";

  // Ícone de calendário
  const calIcon = document.createElement("span");
  calIcon.textContent = "📅";
  calIcon.style.fontSize = "18px";
  roDiv.appendChild(calIcon);

  // Label bold
  const labelSpan = document.createElement("span");
  labelSpan.textContent = "Concluído em:";
  labelSpan.style.fontSize = "14px";
  labelSpan.style.fontWeight = "700";  // **bold**
  labelSpan.style.color = "#374151";
  roDiv.appendChild(labelSpan);


  // Texto da data (normal)
  const text = document.createElement("span");
  text.textContent = dateText;
  text.style.fontSize = "14px";
  text.style.fontWeight = "400";      // normal
  text.style.color = "#374151";
  roDiv.appendChild(text);

  return roDiv;
}

