  labelEl.style.display = "flex";
  labelEl.textContent = label;
  div.style.padding = "0 15px";
  div.style.fontWeight = "bold";
  div.appendChild(labelEl);
}

const input = document.createElement("textarea");

input.setAttribute("name", name);
input.setAttribute("data-itemname", label);
input.setAttribute("data-touched", false);
input.setAttribute("data-id", id);
input.setAttribute("placeholder", "Escreva algo");
input.style.width = "100%";
input.style.height = "60px";
input.style.fontSize = "18px";
input.style.lineHeight = "27px";
input.style.border = "none";
input.style.borderRadius = "8px";
input.style.paddingLeft = "0";
input.style.marginBottom = "15px";
input.style.outline = "none";
input.style.resize = "none";
input.style.fontFamily = "Inter";

if (value) {
  input.value = value;
  requestAnimationFrame(() => {
    input.style.height = "auto";
    input.style.height = `${input.scrollHeight}px`;
  });
}

// auto resize on input
input.oninput = () => {
  input.setAttribute("data-touched", true);
  input.style.height = "auto"; // resetar para recalcular
  input.style.height = `${input.scrollHeight}px`;
};

if (label) {
  div.appendChild(input);
  return div;
}

return input;

