function waitForKanbanContainer(callback) {
  const doc = wwLib.getFrontDocument();
  const container = doc.querySelector('.kanbanContainer');
  if (container) {
    callback(container);
  } else {
    // tenta de novo em 100ms até encontrar
    setTimeout(() => waitForKanbanContainer(callback), 100);
  }
}

waitForKanbanContainer((kanbanContainer) => {
  const threshold = 150;
  const maxSpeed = 25;
  const minSpeed = 8;

  let lastEvent = null;
  let isTicking = false;

  wwLib.getFrontDocument().addEventListener('dragover', (e) => {
    // Se não estiver arrastando, devolve true para permitir o drop nativo
    if (!variables['e4bd14b8-715f-441f-85ea-8a3d867b4e77-isDragging']) {
      return true;
    }

    // Armazena o último evento
    lastEvent = e;

    // Se já programamos um rAF, não agenda outro
    if (!isTicking) {
      isTicking = true;
      requestAnimationFrame(() => {
        const rect = kanbanContainer.getBoundingClientRect();
        const mouseX = lastEvent.clientX;

        const leftDistance = mouseX - rect.left;
        const rightDistance = rect.right - mouseX;

        if (leftDistance < threshold) {
          const speed = Math.max(minSpeed, maxSpeed * ((threshold - leftDistance) / threshold));
          kanbanContainer.scrollLeft -= speed;
        } else if (rightDistance < threshold) {
          const speed = Math.max(minSpeed, maxSpeed * ((threshold - rightDistance) / threshold));
          kanbanContainer.scrollLeft += speed;
        }

        isTicking = false;
      });
    }

    // Precisa retornar false para impedir comportamento padrão?
    // No caso do WeWeb, geralmente deixar “return true” é suficiente para não atrapalhar o drag nativo.
    return true;
  });
});

