const contextId = variables["28bd4345-b91a-42bd-90d7-ce6c5a27966f"];

const loggedUser =
    pluginVariables["1fa0dd68-5069-436c-9a7d-3b54c340f1fa"]["user"]?.["id"];

let integrantes = variables["0da69bf0-371e-4e2b-8c54-4747696d800a"]?.["equipes"]
    .map((e) => {
        if (contextId != "") {
            if (e.urlId == contextId) {
                return e.users;
            }
        } else {
            return e.users;
        }
    })
    .filter((e) => e != undefined);

const flat = integrantes.flat();
const seen = new Set();
let uniqueUsers = [];

flat.forEach((entry) => {
    const user = entry.usuario;
    if (!seen.has(user.id)) {
        seen.add(user.id);
        uniqueUsers.push({ ...user, user_id: entry.user_id });
    }
});

if (contextId == "") {
    uniqueUsers = uniqueUsers.filter((u) => u.user_id == loggedUser);
}

return uniqueUsers;

