const generateInputFields = (fields, form, parentStack = []) => {
  if (!fields || fields.length === 0) return;

  const formElement = resolveElement(form);

  parentStack.push(formElement); // salva o contexto atual

  fields.forEach((item) => {
    if (item.itemDependente) {
      const parent = document.createElement("div");
      const styleList = styles(item.itemDependente.nome);
      console.log(styleList);
      parent.id = item.id;
      parent.classList.add(item.itemDependente.nome);
      parent.style.width = "100%";
      parent.style.position = "relative";

      if (styleList){
        const extraStyles = styleList.styles();
        Object.assign(parent.style, extraStyles);
      }

      // Adiciona ao último pai conhecido
      parentStack[parentStack.length - 1].appendChild(parent);

