const resolveElement = (form) => {
  if (typeof form === "string") {
    const el = document.getElementById(form);
    if (!el) {
      throw new Error(`Elemento com id="${form}" não encontrado no DOM.`);
    }
    return el;
  }
  if (form instanceof HTMLElement) {
