  }
  throw new Error(
    "Parâmetro 'form' precisa ser id (string) ou um HTMLElement válido."
  );
};

const updateInputFields = (fields, formElement, parentStack = []) => {
  // Se não vier array ou for vazio, nada a fazer
  if (!fields || !Array.isArray(fields) || fields.length === 0) {
    return;
  }

  // formElement aqui já é um HTMLElement válido
  parentStack.push(formElement);

  for (const fieldItem of fields) {
    // Caso haja itemDependente, é um contêiner (div) ou botão
    if (fieldItem.itemDependente) {
      if (fieldItem.isButton === true) {
        console.log(data);
        const buttonNode = formulas["0a84e6ec-a91b-4f90-9858-60f07b934c0c"](
          fieldItem,
          data
        );
        if (!(buttonNode instanceof Node)) {
          console.warn(
            `Esperava Node de formulas[0a84e6ec…], recebeu:`,
            buttonNode
          );
        } else {
          parentStack[parentStack.length - 1].appendChild(buttonNode);
        }
      } else {
        // Cria wrapper (div) para agrupar subcampos
        const wrapper = document.createElement("div");
        wrapper.id = String(fieldItem.id);
        wrapper.classList.add(fieldItem.itemDependente.nome);
        wrapper.style.width = "100%";
        wrapper.style.position = "relative";

        // Se precisar armazenar metadados em data-attribute
        const metaDados = data.filter((d) => d.instanceId);
        if (metaDados && metaDados.length) {
          const json = JSON.stringify(metaDados);
          wrapper.dataset.metaDados = btoa(json);
        }

        // Aplica possíveis estilos custom via “styles(nome)”
        const styleList = styles(fieldItem.itemDependente.nome);
        if (styleList && typeof styleList.styles === "function") {
          const extraStyles = styleList.styles();
          Object.assign(wrapper.style, extraStyles);
        }

        parentStack[parentStack.length - 1].appendChild(wrapper);

        // Garante que “dadoDependente” seja tratado como array
        const filhos = Array.isArray(fieldItem.dadoDependente)
          ? fieldItem.dadoDependente
          : [fieldItem.dadoDependente];

        updateInputFields(filhos, wrapper, parentStack);
      }
    }

    else {
      const currentParent = parentStack[parentStack.length - 1];
      updateInputField(fieldItem, currentParent);
    }
  }

  parentStack.pop();
};

