// Fun√ß√£o para padronizar o acesso ao elemento
const resolveElement = (form) => {
  if (typeof form === "string") {
    const el = document.getElementById(form);
    if (!el) throw new Error(`Elemento '${form}' n√£o encontrado no DOM.`);
    return el;
  }else {
    return form;
  }
  throw new Error("Par√¢metro 'form' precisa ser uma string (seletor) ou um HTMLElement.");
};

// Fun√ß√£o principal adaptada
const generateInputFields = (fields, form, parentStack = []) => {
  if (!fields || fields.length === 0) return;

  const formElement = resolveElement(form);

  parentStack.push(formElement); // salva o contexto atual

  fields.forEach((item) => {
    if (item.itemDependente) {
      const parent = document.createElement("div");
      const styleList = styles(item.itemDependente.nome);
      console.log(styleList);
      parent.id = item.id;
      parent.classList.add(item.itemDependente.nome);
      parent.style.width = "100%";
      parent.style.position = "relative";

      if (styleList){
        const extraStyles = styleList.styles();
        Object.assign(parent.style, extraStyles);
      }

      // Adiciona ao √∫ltimo pai conhecido
      parentStack[parentStack.length - 1].appendChild(parent);

      // Chama recursivamente com o novo pai e pilha atualizada
      generateInputFields(item.dadoDependente, parent, parentStack);
    } else {
      const currentParent = parentStack[parentStack.length - 1];
      createInputField(item.dadoDependente, currentParent);
    }
  });

  parentStack.pop(); // volta ao pai anterior
};

const createInputField = (dado, form) => {
  const formElement = resolveElement(form);

  const html = variables["e556e999-bc31-433f-859f-e397706c547b"].find(
    (h) => h.id === dado.campohtml
  );

  if (html) {
    const el = formulas[html.insert](html.id, dado.id, dado.nomedodado);
    formElement.appendChild(el);
  } else {
    console.warn(`HTML n√£o encontrado para o campo: ${dado.nomedodado}`);
  }
};

