preview.style.maxHeight = "200px";
preview.style.borderRadius = "6px";
preview.style.border = "1px solid #ccc";
preview.style.objectFit = "contain";
preview.alt = "Pr√©via da imagem";

// Fundo quadriculado para evitar que imagens transparentes "sumam"
preview.style.background = `repeating-conic-gradient(#eee 0% 25%, transparent 0% 50%) 
                              0% / 20px 20px`;

// Eventos
labelBtn.addEventListener("click", () => input.click());

input.addEventListener("change", () => {
    const file = input.files[0];
    if (file && file.type.startsWith("image/")) {
        const reader = new FileReader();
        reader.onload = (e) => {
            const base64 = e.target.result;

            // Armazena no atributo personalizado
            input.setAttribute("data-base64", base64);

            preview.src = base64;
            preview.style.display = "block";
        };
        reader.readAsDataURL(file);
    } else {
        preview.style.display = "none";
        input.removeAttribute("data-base64");
    }
});

// Se recebeu valor inicial (modo edi√ß√£o)
if (value) {
    preview.src = value;
    preview.style.display = "block";
    input.setAttribute("data-base64", value);
}

// Composi√ß√£o final
wrapper.appendChild(labelBtn);
wrapper.appendChild(input);
wrapper.appendChild(preview);

return wrapper;

